import {
  DELETE_RIFF,
  SAVE_NEW_RIFF,
  SAVE_EDIT_RIFF,
  CANCEL_EDIT,
  RECEIVE_RIFF_LIST,
  SAVE_NEW_RIFF_SUCCESS,
  UPDATE_RIFF_TIME_SUCCESS,
} from '../actions/index.js';

let initialState = [];

const riffsReducer = (state = initialState, action) => {
  switch (action.type) {
    case DELETE_RIFF: {
      let ret = { ...state };

      ret.all = ret.filter((el) => el.id !== action.id);

      // silly change

      /*let index = ret.all.findIndex(el => el.id === action.id);



      ret.all.splice(index, 1);*/

      return ret;
    }
    case RECEIVE_RIFF_LIST:
      //debugger;
      return action.payload.map((el) => ({
          ...el,
          time: el.start,
          payload: el.isText ? el.text : null,
          type: el.isText ? 'text' : 'audio',
        }));

    case UPDATE_RIFF_TIME_SUCCESS:
      let riffs = [...state];
      riffs.forEach((el, ind, arr) => {
        if (el.id === action.id)
          arr[ind] = { ...el, id: action.id, time: Number(action.time) };
        //el.id = action.payload.id;
      });
      return riffs;

    case SAVE_NEW_RIFF: // code block for variable grouping
    {
      const riff =
      {
        ...action.payload,
        saved: false,
        tempId: new Date().getUTCMilliseconds()
      };
      delete riff.payload;

      // create new riffs.all list, including new riff
      let riffs = [...state, riff];

      return riffs;
    }

    case SAVE_EDIT_RIFF:
    {
      const riff = { ...action.payload, saved: false };
      delete riff.payload;

      let riffs = [...state.all];
      riffs[riff.id] = riff;

      return riffs;
    }

    case SAVE_NEW_RIFF_SUCCESS:
    {
      let riffs = [...state];
      riffs.forEach((el, ind, arr) => {
        if (el.tempId === action.payload.tempId)
          arr[ind] = { ...el, id: action.payload.id };
        //el.id = action.payload.id;
      });
      return riffs;
    }
    case SAVE_EDIT_RIFF_SUCCESS:
    {

    }

    default:
      return state;
  }
};

export default riffsReducer;
